function starttimer(t) {
	var e = t.FlipClock({
			clockFace : "HourlyCounter",
			autoStart : !1,
			language : "ru"
		});
	e.setTime(2307),
	e.setCountdown(!0),
	e.start()
}
$(document).ready(function () {
	var t = $(".faq-query");
	t.click(function () {
		$(".faq-answer").stop().animate({
			paddingTop : 0,
			height : 0,
			paddingBottom : 0
		}, 500),
		$(".faq-answer").css("box-shadow", "none");
		var t = $(this).siblings(".faq-answer");
		0 == t.height() ? (h = t.css("height", "auto").height(), t.height(0), t.stop().animate({
				paddingTop : "12px",
				height : h + 24,
				paddingBottom : "12px"
			}, 500), t.css("box-shadow", "-2px 2px 4.9px 0.1px rgba(0, 0, 0, 0.24)")) : (t.stop().animate({
				paddingTop : 0,
				height : 0,
				paddingBottom : 0
			}, 500), setTimeout(function () {
				t.css("box-shadow", "none")
			}, 500))
	}),
	starttimer($(".clock-top")),
	starttimer($(".clock-form01")),
	starttimer($(".clock-form02")),
	starttimer($(".clock-form03")),
	starttimer($(".clock-form04")),
	starttimer($(".clock-form05"))
}), 

$(window).scroll(function () {
	$(".faq-answer").each(function () {
		0 != $(this).height() && ($(this).stop().animate({
				paddingTop : 0,
				height : 0,
				paddingBottom : 0
			}, 500), setTimeout(function () {
				$(this).css("box-shadow", "none")
			}, 500))
	})
});
